INSERT INTO `wp_posts` VALUES (2, 1, '2008-08-24 02:21:01', '2008-08-24 06:21:01', 'Windmill is a web testing framework intended for complete automation of user interface testing, with strong test debugging capabilities.\r\n\r\nThe first goal of Windmill is to make test writing easier, portable and sustainable. Test writing and debugging is an interactive process and requires an architecture that allows for large amounts of flexibility and back-and-forth communication.\r\n\r\nThe second goal of Windmill is to keep the project open and transparent, encouraging as much outside contribution as possible. This goal is shared by every project started at OSAF and we have maintained a reputation of accomplishing transparency in Windmill.', 'Mission', 0, '', 'publish', 'closed', 'open', '', 'about', '', '', '2009-04-22 16:21:17', '2009-04-22 20:21:17', '', 0, 'http://t0asted.com/getwindmill/?page_id=2', 3, 'page', '', 0);
INSERT INTO `wp_posts` VALUES (76, 2, '2007-10-19 14:29:25', '2007-10-19 18:29:25', 'Tuesday, ( October 16th ) we pushed out the 0.2 release of Windmill to the eagerly awaiting world of QA.\r\n\r\nThis release includes many new features and bug fixes:\r\n<ul>\r\n	<li>Windmill Unit Tests, for all your future contributors out there to make sure you didn’t break anything!</li>\r\n	<li>Browser launcher support for Safari and Firefox Linux, no more manual proxy configuration.</li>\r\n	<li>Lots of performance and stability improvement, say goodbye to those t-box hangs.</li>\r\n	<li>The assertion explorer tool, now point and click to create your assertions.</li>\r\n	<li>Javascript tests, write tests in Javascript to test deep down in that app’s source.</li>\r\n	<li>Commenting in all of the test files, including JSON.</li>\r\n	<li>Windmill support for testing against localhost</li>\r\n	<li>Fully functional WIndmill Integrated Development Environment in all the browsers.</li>\r\n	<li>Controller additions of many wait and assert functions, get better control of your tests.</li>\r\n	<li>Convenience features including automatically giving you the correct focus when working in the IDE, smarter logic in the DOM Explorer.</li>\r\n	<li>DOM serialization, you can now pause your tests and get the state of your page on command.</li>\r\n	<li>Cross domain, frame and window recording and testing</li>\r\n	<li>And much more…</li>\r\n</ul>', 'We did it ! 0.2 Released', 0, '', 'publish', 'closed', 'open', '', 'we-did-it-02-released', '', '', '2008-08-26 18:29:21', '2008-08-26 22:29:21', '', 0, 'http://www.getwindmill.com/?p=76', 0, 'post', '', 0);
INSERT INTO `wp_posts` VALUES (77, 2, '2008-08-26 01:11:02', '2008-08-26 05:11:02', '', 'We did it ! 0.2 Released', 0, '', 'inherit', 'closed', 'open', '', '76-revision', '', '', '2008-08-26 01:11:02', '2008-08-26 05:11:02', '', 76, 'http://www.getwindmill.com/?p=77', 0, 'revision', '', 0);
