INSERT INTO `wp_posts` VALUES (78, 2, '2007-10-22 20:39:55', '2007-10-23 00:39:55', 'We just pushed the 0.2.1 bugfix release to the cheeseshop.\r\n\r\nFixes Include:\r\n<ul>\r\n	<li>Fixes to Safari and IE JavaScript based tests</li>\r\n	<li>Fixed some scoping issues in the JavaScript test framework</li>\r\n	<li>Moved "js" directory structure to more standardized "html" dir.</li>\r\n	<li>Fixed some bugs in serializing tests before they are saved and stripping components that don''t need to be saved.</li>\r\n	<li>Fixed a bug in the bdist packaging that wasn''t creating the "saves" directory blocking saving from being usable by bdist builds.</li>\r\n</ul>', 'Windmill 0.2.1 Now Available', 0, '', 'publish', 'closed', 'open', '', 'windmill-021-now-available', '', '', '2008-08-26 18:30:13', '2008-08-26 22:30:13', '', 0, 'http://www.getwindmill.com/?p=78', 0, 'post', '', 0);
