INSERT INTO `wp_posts` VALUES (213, 1, '2008-08-26 00:09:27', '2008-08-26 04:09:27', 'Windmill is a web testing framework intended for complete automation of user interface testing, with strong test debugging capabilities.\r\n\r\nThe first goal of Windmill to make test writing easier, portable and sustainable. Test writing and debugging is an interactive process and requires an architecture that allows for large amounts of flexibility and back-and-forth communication.\r\n\r\nThe second goal of Windmill is to keep the project open and transparent, encouraging as much outside contribution as possible. This goal is shared by every project started at OSAF and we have maintained a reputation of accomplishing transparency in our projects.', 'Mission', 0, '', 'inherit', 'closed', 'open', '', '2-revision-5', '', '', '2008-08-26 00:09:27', '2008-08-26 04:09:27', '', 2, 'http://www.getwindmill.com/archives/213', 0, 'revision', '', 0);
