INSERT INTO `wp_posts` VALUES (216, 1, '2008-08-27 15:39:49', '2008-08-27 19:39:49', '<table border="0">\r\n<tbody>\r\n<tr>\r\n<td><strong>Supported Browsers\r\n </strong></td>\r\n<td><img class="alignright size-medium wp-image-163" title="Firefox" src="http://www.getwindmill.com/wp-content/uploads/2008/08/firefox.png" alt="Firefox" width="48" height="46" /><img class="alignright size-medium wp-image-164" title="Safari" src="http://www.getwindmill.com/wp-content/uploads/2008/08/safari.png" alt="Safari" width="41" height="45" /><img class="alignright size-medium wp-image-165" title="Internet Explorer" src="http://www.getwindmill.com/wp-content/uploads/2008/08/ie.png" alt="Internet Explorer" width="48" height="46" /><img class="alignright size-medium wp-image-166" title="Opera" src="http://www.getwindmill.com/wp-content/uploads/2008/08/opera.png" alt="Opera" width="48" height="42" /></td>\r\n</tr>\r\n</tbody></table>\r\n<strong>Comparison Chart</strong>\r\n<table border="1">\r\n<tbody>\r\n<tr>\r\n<td><strong>Feature</strong></td>\r\n<td><strong>Watir</strong></td>\r\n<td><strong>Selenium</strong></td>\r\n<td style="background-color: #fffbcc;"><strong>Windmill</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Browser based</td>\r\n<td><strong>yes </strong>(Privileged)</td>\r\n<td><strong>yes </strong>(Content)</td>\r\n<td style="background-color: #fffbcc;"><strong>yes </strong>(Content)</td>\r\n</tr>\r\n<tr>\r\n<td>Browser type independent</td>\r\n<td>Different project per browser</td>\r\n<td><strong>yes</strong></td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong> (Firefox, Safari, IE, Opera)</td>\r\n</tr>\r\n<tr>\r\n<td>Recorder</td>\r\n<td><strong>yes</strong></td>\r\n<td>Only on Firefox.</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong> (DOM, Assertion Explorers in IDE)</td>\r\n</tr>\r\n<tr>\r\n<td>Simultaneuos playback</td>\r\n<td><strong>yes</strong></td>\r\n<td>no</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Frames and popup support</td>\r\n<td><strong>yes</strong></td>\r\n<td>has problems</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n<tr>\r\n<td>SSL Support</td>\r\n<td><strong>yes</strong></td>\r\n<td>?</td>\r\n<td style="background-color: #fffbcc;">Solution In Progress</td>\r\n</tr>\r\n<tr>\r\n<td>Learning curve</td>\r\n<td>Learn Ruby</td>\r\n<td>?</td>\r\n<td style="background-color: #fffbcc;">Graphical recorder, saving and playback</td>\r\n</tr>\r\n<tr>\r\n<td>Languages</td>\r\n<td>Ruby</td>\r\n<td>Many, HTML Tables</td>\r\n<td style="background-color: #fffbcc;">(None required to be productive), Optional: Python, JavaScript, JSON</td>\r\n</tr>\r\n<tr>\r\n<td>Web-app independent</td>\r\n<td><strong>yes</strong></td>\r\n<td>no - needs to be deployed with app</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n</tbody></table>\r\n \r\n<table border="0">\r\n<tbody>\r\n<tr>\r\n<td><img class="alignright size-medium wp-image-44" title="recorder" src="http://www.getwindmill.com/wp-content/uploads/2008/08/recorder.png" alt="" width="377" height="52" /></td>\r\n</tr>\r\n<tr>\r\n<td><strong>Integrated Development Environment</strong>\r\n- Full Featured Recorder: One click and the IDE writes your test for you\r\n- DOM Explorer: Explore the DOM, aids in test editing and debugging\r\n- Assertion Explorer: Quickly generate action validating the state of your application\r\n- Test Saving and Playback\r\n- Real Time Performance and Output Information</td>\r\n</tr>\r\n<tr>\r\n<td> <a href="http://www.getwindmill.com/wp-content/uploads/2008/08/wmfblite.png"><img class="alignnone size-medium wp-image-156" title="Firebug Lite Integration" src="http://www.getwindmill.com/wp-content/uploads/2008/08/wmfblite.png" alt="Firebug Lite Integration" width="366" height="90" /></a></td>\r\n</tr>\r\n<tr>\r\n<td>- Firebug Lite integration for debugging in all supported browsers</td>\r\n</tr>\r\n<tr>\r\n<td><strong>\r\n</strong><img class="alignright size-medium wp-image-51" title="Windmill Shell" src="http://www.getwindmill.com/wp-content/uploads/2008/08/picture-1.png" alt="" width="326" height="106" /></td>\r\n</tr>\r\n<tr>\r\n<td><strong>Interactive Python Server</strong> \r\n- Convenience functionality for launching browsers\r\n- PDB debugging support\r\n- Test loading and running\r\n- DOM query functionality\r\n- Reporting helpers</td>\r\n</tr>\r\n</tbody></table>', 'Features', 0, '', 'inherit', 'closed', 'open', '', '20-revision-55', '', '', '2008-08-27 15:39:49', '2008-08-27 19:39:49', '', 20, 'http://www.getwindmill.com/archives/216', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES (211, 1, '2008-09-02 19:00:27', '2008-09-02 23:00:27', 'Today we released Windmill 0.8.2 which includes some important bug fixes, and very exciting feature work.\n\n<strong>Features include</strong>:\n<ul>\n	<li> Addition of firebug lite support in IDE and Testing Application</li>\n	<li> Fully functional wxWindmill contrib application</li>\n	<li> Enhanced ''smart'' recorder</li>\n	<li> Auto element scroll into view to more closely simulate users</li>\n</ul>\n<strong>Bug Fixes include</strong>:\n<ul>\n	<li> <a href="http://trac.getwindmill.com/timeline">http://trac.getwindmill.com/ticket/135</a></li>\n	<li> Windmill defaulting to timeout for starting service loop</li>\n	<li> Many others, view the <a href="http://trac.getwindmill.com/timeline">timeline</a></li>\n</ul>\nWe feel 0.8.2 is a very stable and useable release so we are going to begin our effort to get the word out about the project. To aid in this effort we now have a <a href="http://twitter.com/windmillproject">Windmill Twitter Feed</a> to keep you up to date on new releases, events, blog announcements etc.\n\nTo start using wxWindmill, please refer to the <a href="http://trac.getwindmill.com/wiki/wxWindmill">wxWidmill Wiki Page</a>\n\n<strong>Update!</strong>\n<code>sudo easy_install -U windmill</code>', 'Windmill 0.8.2 Released', 0, '', 'inherit', 'closed', 'open', '', '210-revision', '', '', '2008-09-02 19:00:27', '2008-09-02 23:00:27', '', 210, 'http://www.getwindmill.com/archives/211', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES (212, 1, '2008-09-02 19:00:46', '2008-09-02 23:00:46', 'Today we released Windmill 0.8.2 which includes some important bug fixes, and very exciting feature work.\r\n\r\n<strong>Features include</strong>:\r\n<ul>\r\n	<li> Addition of firebug lite support in IDE and Testing Application</li>\r\n	<li> Fully functional wxWindmill contrib application</li>\r\n	<li> Enhanced ''smart'' recorder</li>\r\n	<li> Auto element scroll into view to more closely simulate users</li>\r\n</ul>\r\n<strong>Bug Fixes include</strong>:\r\n<ul>\r\n	<li> <a href="http://trac.getwindmill.com/timeline">http://trac.getwindmill.com/ticket/135</a></li>\r\n	<li> Windmill defaulting to timeout for starting service loop</li>\r\n	<li> Many others, view the <a href="http://trac.getwindmill.com/timeline">timeline</a></li>\r\n</ul>\r\nWe feel 0.8.2 is a very stable and useable release so we are going to begin our effort to get the word out about the project. To aid in this effort we now have a <a href="http://twitter.com/windmillproject">Windmill Twitter Feed</a> to keep you up to date on new releases, events, blog announcements etc.\r\n\r\nTo start using wxWindmill, please refer to the <a href="http://trac.getwindmill.com/wiki/wxWindmill">wxWidmill Wiki Page</a>\r\n\r\n<strong>Update!</strong>\r\n<code>sudo easy_install -U windmill</code>', 'Windmill 0.8.2 Released', 0, '', 'inherit', 'closed', 'open', '', '210-revision-2', '', '', '2008-09-02 19:00:46', '2008-09-02 23:00:46', '', 210, 'http://www.getwindmill.com/archives/212', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES (20, 1, '2008-08-25 23:21:11', '2008-08-26 03:21:11', '<table border="0">\r\n<tbody>\r\n<tr>\r\n<td><strong>Supported Browsers\r\n</strong></td>\r\n<td><img id="firefoxImg" class="alignright size-medium wp-image-163" title="Firefox" src="http://www.getwindmill.com/wp-content/uploads/2008/08/firefox.png" alt="Firefox" width="48" height="46" /><img id="safariImg" class="alignright size-medium wp-image-164" title="Safari" src="http://www.getwindmill.com/wp-content/uploads/2008/08/safari.png" alt="Safari" width="41" height="45" /><img class="alignright size-medium wp-image-165" title="Internet Explorer" src="http://www.getwindmill.com/wp-content/uploads/2008/08/ie.png" alt="Internet Explorer" width="48" height="46" /><img class="alignnone size-full wp-image-351" title="Google Chrome" src="http://www.getwindmill.com/wp-content/uploads/2008/12/chrome.gif" alt="" /><img class="alignright size-medium wp-image-166" title="Opera" src="http://www.getwindmill.com/wp-content/uploads/2008/08/opera.png" alt="Opera" width="48" height="42" /></td>\r\n</tr>\r\n</tbody></table>\r\n<strong>Comparison Chart</strong>\r\n<table id="basic-table" border="1">\r\n<tbody>\r\n<tr>\r\n<td style="font-weight:bold;">Feature</td>\r\n<td style="font-weight:bold;">Windmill</td>\r\n<td style="font-weight:bold;">Selenium</td>\r\n<td style="font-weight:bold;">Watir</td>\r\n</tr>\r\n<tr>\r\n<td style="color:#068481;">IE/Safari/Firefox/Chrome Support</td>\r\n<td>Yes</td>\r\n<td>Yes</td>\r\n<td><a href="http://wtr.rubyforge.org/platforms.html">Differing libraries</a></td>\r\n</tr>\r\n<tr>\r\n<td style="color:#068481;">Test Recorder</td>\r\n<td>Yes, on all platforms</td>\r\n<td>Firefox Plugin only</td>\r\n<td>Yes</td>\r\n</tr>\r\n<tr>\r\n<td style="color:#068481;">Cross Domain</td>\r\n<td>Yes</td>\r\n<td>Firefox and IE only in trusted mode.</td>\r\n<td>Yes</td>\r\n</tr>\r\n<tr>\r\n<td style="color:#068481;">SSL</td>\r\n<td>Yes</td>\r\n<td>Yes</td>\r\n<td>Yes</td>\r\n</tr>\r\n<t