INSERT INTO `wp_posts` VALUES (88, 2, '2007-11-01 16:45:07', '2007-11-01 20:45:07', 'Now available in the cheeseshop.\r\n\r\nFixes Include:\r\n<ul>\r\n	<li>Added configurable global timeouts for Windmill controller functions</li>\r\n	<li>IDE playback now functions as expected, stop clears the service queue.</li>\r\n	<li>Fixed Windmill IDE action/scroll bar overlap issues</li>\r\n	<li>Added auto collapsing suite functionality</li>\r\n	<li>Cross frame recording and playback now work OOTB.</li>\r\n	<li>IE6/IE7 launchers are again working as expected</li>\r\n</ul>', 'Windmill 0.2.6 Now Available', 0, '', 'publish', 'closed', 'open', '', 'windmill-026-now-available', '', '', '2008-08-26 18:34:02', '2008-08-26 22:34:02', '', 0, 'http://www.getwindmill.com/?p=88', 0, 'post', '', 0);
