INSERT INTO `wp_posts` VALUES (90, 2, '2008-11-05 11:24:44', '2008-11-05 15:24:44', 'Tomorrow we''re having an IRC sprint for windmill.\r\n\r\nThe windmill developers will be online in #windmill on irc.freenode.org from 10am to 5pm PST.\r\n\r\nEven if you haven''t tried to install windmill before, come and give us feedback through the entire process and we''ll be there to help if you run in to any problems.\r\n\r\nHope to see you there.\r\n\r\n-Mikeal', 'IRC Sprint Tomorrow, Tuesday, November 6th 2008', 0, '', 'publish', 'closed', 'open', '', 'irc-sprint-tomorrow-tuesday-november-6th-2007', '', '', '2008-11-05 18:07:38', '2008-11-05 22:07:38', '', 0, 'http://www.getwindmill.com/?p=90', 0, 'post', '', 0);
