INSERT INTO `wp_posts` VALUES (114, 2, '2008-08-26 00:34:42', '2008-08-26 04:34:42', '<strong>Comparison Chart</strong>\r\n<table border="1">\r\n<tbody>\r\n<tr>\r\n<td><strong>Feature</strong></td>\r\n<td><strong>Watir</strong></td>\r\n<td><strong>Selenium</strong></td>\r\n<td style="background-color: #fffbcc;"><strong>Windmill</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Browser based</td>\r\n<td><strong>yes </strong>(Privileged)</td>\r\n<td><strong>yes </strong>(Content)</td>\r\n<td style="background-color: #fffbcc;"><strong>yes </strong>(Content)</td>\r\n</tr>\r\n<tr>\r\n<td>Browser type independent</td>\r\n<td>Different project per browser</td>\r\n<td><strong>yes</strong></td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong> (Firefox, Safari, IE, Opera)</td>\r\n</tr>\r\n<tr>\r\n<td>Recorder</td>\r\n<td><strong>yes</strong></td>\r\n<td>Only on Firefox.</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong> (DOM Explorer, Assertion Explorer in IDE)</td>\r\n</tr>\r\n<tr>\r\n<td>Simultaneuos playback</td>\r\n<td><strong>yes</strong></td>\r\n<td>no</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Frames and popup support</td>\r\n<td><strong>yes</strong></td>\r\n<td>has problems</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n<tr>\r\n<td>SSL Support</td>\r\n<td><strong>yes</strong></td>\r\n<td>?</td>\r\n<td style="background-color: #fffbcc;"><strong>Solution In Progress</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Learning curve</td>\r\n<td>Learn Ruby</td>\r\n<td>?</td>\r\n<td style="background-color: #fffbcc;">Python, JavaScript, JSON</td>\r\n</tr>\r\n<tr>\r\n<td>Web-app independent</td>\r\n<td><strong>yes</strong></td>\r\n<td>no - needs to be deployed with app</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n</tbody></table>\r\n\r\n</br><table>\r\n<tr>\r\n<td>\r\n<img class="alignright size-medium wp-image-44" title="recorder" src="http://www.getwindmill.com/wp-content/uploads/2008/08/recorder.png" alt="" />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td><strong>Integrated Development Environment</strong></br>\r\n- Full Featured Recorder: One click and the IDE writes your test for you\r\n- DOM Explorer: Explore the DOM, aids in test editing and debugging\r\n- Assertion Explorer: Quickly generate action validating the state of your application\r\n- Test Saving and Playback\r\n- Cross Browser Compatible Shell\r\n- Real Time Performance and Output Information\r\n</td>\r\n</tr>\r\n<tr><td>&nbsp;</td></tr>\r\n<tr>\r\n<td>\r\n<img class="alignright size-medium wp-image-51" title="Windmill Shell" src="http://www.getwindmill.com/wp-content/uploads/2008/08/picture-1.png" alt="" />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<strong>Interactive Python Server</strong>\r\n- Convenience functionality for launching browsers\r\n- PDB debugging support\r\n- Test loading and running\r\n- DOM query functionality\r\n- Reporting helpers\r\n</td>\r\n</tr>\r\n</table>', 'Features', 0, '', 'inherit', 'closed', 'open', '', '20-revision-35', '', '', '2008-08-26 00:34:42', '2008-08-26 04:34:42', '', 20, 'http://www.getwindmill.com/archives/114', 0, 'revision', '', 0);
