INSERT INTO `wp_posts` VALUES (352, 2, '2008-10-11 17:18:11', '2008-10-11 21:18:11', '<table border="0">\r\n<tbody>\r\n<tr>\r\n<td><strong>Supported Browsers\r\n </strong></td>\r\n<td><img id="firefoxImg" alt="Firefox" class="alignright size-medium wp-image-163" title="Firefox" src="http://www.getwindmill.com/wp-content/uploads/2008/08/firefox.png" alt="Firefox" width="48" height="46" /><img id="safariImg" alt="Safari" class="alignright size-medium wp-image-164" title="Safari" src="http://www.getwindmill.com/wp-content/uploads/2008/08/safari.png" alt="Safari" width="41" height="45" /><img class="alignright size-medium wp-image-165" title="Internet Explorer" src="http://www.getwindmill.com/wp-content/uploads/2008/08/ie.png" alt="Internet Explorer" width="48" height="46" /><img class="alignright size-medium wp-image-166" title="Opera" src="http://www.getwindmill.com/wp-content/uploads/2008/08/opera.png" alt="Opera" width="48" height="42" /></td>\r\n</tr>\r\n</tbody></table>\r\n<strong>Comparison Chart</strong>\r\n<table border="1">\r\n<tbody>\r\n<tr>\r\n<td><strong>Feature</strong></td>\r\n<td><strong>Watir</strong></td>\r\n<td><strong>Selenium</strong></td>\r\n<td style="background-color: #fffbcc;"><strong>Windmill</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Browser based</td>\r\n<td><strong>yes </strong>(Privileged)</td>\r\n<td><strong>yes </strong>(Content)</td>\r\n<td style="background-color: #fffbcc;"><strong>yes </strong>(Content)</td>\r\n</tr>\r\n<tr>\r\n<td>Browser type independent</td>\r\n<td>Different project per browser</td>\r\n<td><strong>yes</strong></td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong> (Firefox, Safari, IE, Opera)</td>\r\n</tr>\r\n<tr>\r\n<td>Recorder</td>\r\n<td><strong>yes</strong></td>\r\n<td>Only on Firefox.</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong> (DOM, Assertion Explorers in IDE)</td>\r\n</tr>\r\n<tr>\r\n<td>Simultaneuos playback</td>\r\n<td><strong>yes</strong></td>\r\n<td>no</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Frames and popup support</td>\r\n<td><strong>yes</strong></td>\r\n<td>has problems</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n<tr>\r\n<td>SSL Support</td>\r\n<td><strong>yes</strong></td>\r\n<td>?</td>\r\n<td style="background-color: #fffbcc;">Solution In Progress</td>\r\n</tr>\r\n<tr>\r\n<td>Learning curve</td>\r\n<td>Learn Ruby</td>\r\n<td>?</td>\r\n<td style="background-color: #fffbcc;">Graphical recorder, saving and playback</td>\r\n</tr>\r\n<tr>\r\n<td>Languages</td>\r\n<td>Ruby</td>\r\n<td>Many, HTML Tables</td>\r\n<td style="background-color: #fffbcc;">(None required to be productive), Optional: Python, JavaScript, JSON</td>\r\n</tr>\r\n<tr>\r\n<td>Web-app independent</td>\r\n<td><strong>yes</strong></td>\r\n<td>no - needs to be deployed with app</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n</tbody></table>\r\n \r\n<table border="0">\r\n<tbody>\r\n<tr>\r\n<td><img class="alignright size-medium wp-image-44" title="recorder" src="http://www.getwindmill.com/wp-content/uploads/2008/08/recorder.png" alt="" width="377" height="52" /></td>\r\n</tr>\r\n<tr>\r\n<td><strong>Integrated Development Environment</strong>   \r\n<ul>\r\n	<li>Full Featured Recorder: One click and the IDE writes your test for you</li>\r\n	<li>DOM Explorer: Explore the DOM, aids in test editing and debugging</li>\r\n	<li>Assertion Explorer: Quickly generate action validating the state of your application</li>\r\n	<li>Test Saving and Playback</li>\r\n	<li>Real Time Performance and Output Information</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td> <a href="http://www.getwindmill.com/wp-content/uploads/2008/08/wmfblite.png"><img class="alignnone size-medium wp-image-156" title="Firebug Lite Integration" src="http://www.getwindmill.com/wp-content/uploads/2008/08/wmfblite.png" alt="Firebug Lite Integration" width="366" height="90" /></a></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<ul>\r\n	<li>Firebug Lite integration for debugging in all supported browsers</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td><strong>\r\n</strong><img class="alignright size-medium wp-image-51" title="Windmill Shell" src="http://www.getwindmill.com/wp-content/uploads/2008/08/picture-1.png" alt="" width="326" height="106" /></td>\r\n</tr>\r\n<tr>\r\n<td><strong>Interactive Python Server</strong>    \r\n<ul>\r\n	<li>Convenience functionality for launching browsers</li>\r\n	<li>PDB debugging support</li>\r\n	<li>Test loading and running</li>\r\n	<li>DOM query functionality</li>\r\n	<li>Reporting helpers</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://www.getwindmill.com/wp-content/uploads/2008/09/wxwindmill.png"><img class="alignnone size-medium wp-image-215" title="wxWindmill Service UI" src="http://www.getwindmill.com/wp-content/uploads/2008/09/wxwindmill.png" alt="wxWindmill Service UI" width="307" height="211" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><strong>wxWindmill Service UI</strong> \r\n<ul>\r\n	<li> Running test files, and test directories</li>\r\n	<li> Loading test files, and test directories</li>\r\n	<li> Launching Firefox, Safari and Internet Explorer</li>\r\n	<li> Killing the open Windmill test browser instances</li>\r\n	<li> Clearing the Test Queue</li>\r\n	<li> A built in Python Shell</li>\r\n	<li> A scoped Python Shell to Windmill</li>\r\n	<li> A fun little GUI to complete your test development experience...</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n</tbody></table>', 'Features', 0, '', 'inherit', 'closed', 'open', '', '20-revision-57', '', '', '2008-10-11 17:18:11', '2008-10-11 21:18:11', '', 20, 'http://www.getwindmill.com/archives/352', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES (334, 2, '2008-12-16 22:18:56', '2008-12-17 02:18:56', 'We are another step closer to 1.0, if you have been watching the <a href="http://www.getwindmill.com/documentation/mailing-lists">mailing lists</a> or the<a href="http://trac.getwindmill.com/timeline"> trac timeline</a> you already know that we have had some serious bug fixes over the past couple weeks as well as one new feature.\r\n\r\n<strong>Bug Fixes:</strong>\r\n<ul>\r\n	<li>Major fixes and updates for the Windmill Unit Tests</li>\r\n	<li>IE fixes for: Drag and Drop, Click, UI usability, Non Compress deps broken</li>\r\n	<li>JS Test Framework bug fixes, JUM assertions, unit tests,</li>\r\n	<li>Improved XPath Generation and bug fixes</li>\r\n</ul>\r\n<strong>Feature</strong>\r\n<ul>\r\n	<li>Full support for launching and killing Google Chrome on Windows (requires Python 2.6)</li>\r\n</ul>\r\nWe really appreciate all the bug reports and emails from you all, thanks for your patience as we try to squash all the bugs.\r\n\r\nAn additional encouraging note is that we got some great PR this week, which can be found here: <a href="http://sdtimes.com/link/33107">Windmill Article in the SDTimes</a>.\r\n\r\nHappy testing.', 'Windmill 1.0beta2 Released', 0, '', 'publish', 'closed', 'open', '', 'windmill-10beta2-released', '', '\nhttp://www.getwindmill.com/documentation/mailing-lists', '2008-12-17 23:04:15', '2008-12-18 03:04:15', '', 0, 'http://www.getwindmill.com/?p=334', 0, 'post', '', 2);
INSERT INTO `wp_posts` VALUES (335, 2, '2008-12-16 22:18:47', '2008-12-17 02:18:47', 'We are another step closer to 1.0, if you have been watching the <a href="http://www.getwindmill.com/documentation/mailing-lists">mailing lists</a> or the<a href="http://trac.getwindmill.com/timeline"> trac timeline</a> you already know that we have had some serious bug fixes over the past couple weeks as well as one new feature.\n\n<strong>Bug Fixes:</strong>\n<ul>\n	<li>Major fixes and updates for the Windmill Unit Tests</li>\n	<li>IE fixes for: Drag and Drop, Click, UI usability, Non Compress deps broken</li>\n	<li>JS Test Framework bug fixes, JUM assertions, unit tests,</li>\n	<li>Improved XPath Generation and bug fixes</li>\n</ul>\n<strong>Feature</strong>\n<ul>\n	<li>Full support for launching and killing Google Chrome on Windows (requires Python 2.6)</li>\n</ul>\nWe really appreciate all the bug reports and emails from you all, thanks for your patience as we try to squash all the bugs.\n\nAn additional encouraging note is that we got some great PR this week, which can be found here: <a href="http://sdtimes.com/link/33107">Windmill Article in the SDTimes</a>.\n\nHappy testing.', 'Windmill 1.0beta2 Released', 0, '', 'inherit', 'closed', 'open', '', '334-revision', '', '', '2008-12-16 22:18:47', '2008-12-17 02:18:47', '', 334, 'http://www.getwindmill.com/archives/335', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES (353, 2, '2008-12-18 18:36:14', '2008-12-18 22:36:14', '<table border="0">\r\n<tbody>\r\n<tr>\r\n<td><strong>Supported Browsers\r\n </strong></td>\r\n<td><img id="firefoxImg" class="alignright size-medium wp-image-163" title="Firefox" src="http://www.getwindmill.com/wp-content/uploads/2008/08/firefox.png" alt="Firefox" width="48" height="46" /><img id="safariImg" class="alignright size-medium wp-image-164" title="Safari" src="http://www.getwindmill.com/wp-content/uploads/2008/08/safari.png" alt="Safari" width="41" height="45" /><img class="alignright size-medium wp-image-165" title="Internet Explorer" src="http://www.getwindmill.com/wp-content/uploads/2008/08/ie.png" alt="Internet Explorer" width="48" height="46" /><img class="alignnone size-full wp-image-351" title="Google Chrome" src="http://www.getwindmill.com/wp-content/uploads/2008/12/chrome.gif" alt="" /><img class="alignright size-medium wp-image-166" title="Opera" src="http://www.getwindmill.com/wp-content/uploads/2008/08/opera.png" alt="Opera" width="48" height="42" /></td>\r\n</tr>\r\n</tbody></table>\r\n<strong>Comparison Chart</strong>\r\n<table border="1">\r\n<tbody>\r\n<tr>\r\n<td><strong>Feature</strong></td>\r\n<td><strong>Watir</strong></td>\r\n<td><strong>Selenium</strong></td>\r\n<td style="background-color: #fffbcc;"><strong>Windmill</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Browser based</td>\r\n<td><strong>yes </strong>(Privileged)</td>\r\n<td><strong>yes </strong>(Content)</td>\r\n<td style="background-color: #fffbcc;"><strong>yes </strong>(Content)</td>\r\n</tr>\r\n<tr>\r\n<td>Browser type independent</td>\r\n<td>Different project per browser</td>\r\n<td><strong>yes</strong></td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong> (Firefox, Safari, IE, Opera)</td>\r\n</tr>\r\n<tr>\r\n<td>Recorder</td>\r\n<td><strong>yes</strong></td>\r\n<td>Only on Firefox.</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong> (DOM, Assertion Explorers in IDE)</td>\r\n</tr>\r\n<tr>\r\n<td>Simultaneuos playback</td>\r\n<td><strong>yes</strong></td>\r\n<td>no</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Frames and popup support</td>\r\n<td><strong>yes</strong></td>\r\n<td>has problems</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n<tr>\r\n<td>SSL Support</td>\r\n<td><strong>yes</strong></td>\r\n<td>?</td>\r\n<td style="background-color: #fffbcc;">Solution In Progress</td>\r\n</tr>\r\n<tr>\r\n<td>Learning curve</td>\r\n<td>Learn Ruby</td>\r\n<td>?</td>\r\n<td style="background-color: #fffbcc;">Graphical recorder, saving and playback</td>\r\n</tr>\r\n<tr>\r\n<td>Languages</td>\r\n<td>Ruby</td>\r\n<td>Many, HTML Tables</td>\r\n<td style="background-color: #fffbcc;">(None required to be productive), Optional: Python, JavaScript, JSON</td>\r\n</tr>\r\n<tr>\r\n<td>Web-app independent</td>\r\n<td><strong>yes</strong></td>\r\n<td>no - needs to be deployed with app</td>\r\n<td style="background-color: #fffbcc;"><strong>yes</strong></td>\r\n</tr>\r\n</tbody></table>\r\n \r\n<table border="0">\r\n<tbody>\r\n<tr>\r\n<td><img class="alignright size-medium wp-image-44" title="recorder" src="http://www.getwindmill.com/wp-content/uploads/2008/08/recorder.png" alt="" width="377" height="52" /></td>\r\n</tr>\r\n<tr>\r\n<td><strong>Integrated Development Environment</strong>    \r\n<ul>\r\n	<li>Full Featured Recorder: One click and the IDE writes your test for you</li>\r\n	<li>DOM Explorer: Explore the DOM, aids in test editing and debugging</li>\r\n	<li>Assertion Explorer: Quickly generate action validating the state of your application</li>\r\n	<li>Test Saving and Playback</li>\r\n	<li>Real Time Performance and Output Information</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td> <a href="http://www.getwindmill.com/wp-content/uploads/2008/08/wmfblite.png"><img class="alignnone size-medium wp-image-156" title="Firebug Lite Integration" src="http://www.getwindmill.com/wp-content/uploads/2008/08/wmfblite.png" alt="Firebug Lite Integration" width="366" height="90" /></a></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<ul>\r\n	<li>Firebug Lite integration for debugging in all supported browsers</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td><strong>\r\n</strong><img class="alignright size-medium wp-image-51" title="Windmill Shell" src="http://www.getwindmill.com/wp-content/uploads/2008/08/picture-1.png" alt="" width="326" height="106" /></td>\r\n</tr>\r\n<tr>\r\n<td><strong>Interactive Python Server</strong>     \r\n<ul>\r\n	<li>Convenience functionality for launching browsers</li>\r\n	<li>PDB debugging support</li>\r\n	<li>Test loading and running</li>\r\n	<li>DOM query functionality</li>\r\n	<li>Reporting helpers</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://www.getwindmill.com/wp-content/uploads/2008/09/wxwindmill.png"><img class="alignnone size-medium wp-image-215" title="wxWindmill Service UI" src="http://www.getwindmill.com/wp-content/uploads/2008/09/wxwindmill.png" alt="wxWindmill Service UI" width="307" height="211" /></a></td>\r\n</tr>\r\n<tr>\r\n<td><strong>wxWindmill Service UI</strong>  \r\n<ul>\r\n	<li> Running test files, and test directories</li>\r\n	<li> Loading test files, and test directories</li>\r\n	<li> Launching Firefox, Safari and Internet Explorer</li>\r\n	<li> Killing the open Windmill test browser instances</li>\r\n	<li> Clearing the Test Queue</li>\r\n	<li> A built in Python Shell</li>\r\n	<li> A scoped Python Shell to Windmill</li>\r\n	<li> A fun little GUI to complete your test development experience...</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n</tbody></table>', 'Features', 0, '', 'inherit', 'closed', 'open', '', '20-revision-58', '', '', '2008-12-18 18:36:14', '2008-12-18 22:36:14', '', 20, 'http://www.getwindmill.com/archives/353', 0, 'revision', '', 0);
